JSON( "/info/filaatendimento/getDadosSolicitante",  {PesquisaNome : $('#solicitante').val()}, function( data, status, xhr ) {          cache[ term ] = data;          response( data );        });		      }    })		.autocomplete( "instance" )._renderItem = function( ul, item ) {      return $( "<li>" )        .append( "<div>" + item.label + "</div>" )        .appendTo( ul );    };  } );  </script>    <script>  $( function() {    var cache = {};    $( "#ocupacao" ) .autocomplete({      minLength: 0,      source: function( request, response ) {        var term = request.term;        if ( term in cache ) {          response( cache[ term ] );          return;        } 		$.getJSON( "/info/filaatendimento/getOcupacao",  {PesquisaNome : $('#ocupacao').val()}, function( data, status, xhr ) {          cache[ term ] = data;          response( data );        });		      }    })		.autocomplete( "instance" )._renderItem = function( ul, item ) {      return $( "<li>" )        .append( "<div>" + item.label + "</div>" )        .appendTo( ul );    };  } );  </script>  <?php echo $_smarty_tpl->getSubTemplate ("../../master/renderBottomBody.tpl", $_smarty_tpl->cache_id, $_smarty_tpl->compile_id, null, null, array(), 0);?>
